import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";

export default class Category extends Component {
  constructor(props){
      super(props);
      this.state = {
        categories: [
          { CategoryId: 1, CategoryName: "ebru" },
          { CategoryId: 2, CategoryName: "arslan" }
        ],
        currentCategory:""
      };
  }

  //changeCategory = () bu bir function demek { }ise içindekileri alır.
  changeCategory = category => {
        this.setState({currentCategory: category.CategoryName})

  }
  render() {
    return (
      <div>
        <h3>{this.props.Info.title}</h3>
        <ListGroup>
          {this.state.categories.map((category) => (
            <ListGroupItem onClick={() =>this.changeCategory(category)} key={category.CategoryId}> {category.CategoryName}
            </ListGroupItem>
          ))}
        </ListGroup>
          <h3>{this.setState.currentCategory}</h3>
      </div>
    );
  }
}
